import React, { Component } from 'react';
import './WeatherApp.css';

// import Form from './Form/Form';
import WeatherConditions from './WeatherConditions/WeatherConditions';

class WeatherApp extends Component {
	constructor() {
		super();
		this.state = {
			city: '',
			country: '',
		}
	}

	handleChange(event) {
		this.setState({value: event.target.value});
	}

	// handleSubmit(event) {
	// 	alert("A city and country were submitted: " + this.state.city, this.state.country);
	// 	event.preventDefault();
	// }

	getWeather = async (e) => {
		// API key
		// let Api_Key = 9b6146ed56aeecdad2247aa0eea8aded;
		e.preventDefault();
		const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=london,uk&appid=9b6146ed56aeecdad2247aa0eea8aded`);
		const response = await api_call.json();
		console.log(response);
	}

	render() {
		return(
			<div>
				<h3>Weather App</h3>
				{/* <Form loadWeather={this.getWeather}/> */}
				<form onSubmit = {this.getdWeather}>
					<input 
						type="text" 
						name="city" 
						placeholder="City..." 
						onChange={this.handleChange} 
						value={this.state.city}/>
					<input
					 type="text" 
					 name="country" 
					 placeholder="Country..." 
					 onChange={this.handleChange} 
					 value={this.state.country}/>
					<button>Get Weather</button>
				</form>
				<WeatherConditions/>
			</div>
		);
	}
}

export default WeatherApp;
