import React, { Component } from 'react';
// import './WeatherApp.css';

const Form = props => {

	handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
	};

		return (
			<form onSubmit={props.getWeather}>
				<input 
					type="text" 
					name="city" 
					placeholder="City..." 
					onChange={this.handleChange} 
					value={props.city}/>
				<input
					type="text" 
					name="country" 
					placeholder="Country..." 
					onChange={this.handleChange} 
					value={props.country}/>
				<button type="submit">Get Weather</button>
			</form>
		);
	}


export default Form;
