import React, { Component } from 'react';
import './WeatherApp.css';

import Form from './Form/Form';
import WeatherConditions from './WeatherConditions/WeatherConditions';

class WeatherApp extends Component {
	constructor(props) {
		super(props);
		this.state = {
			city: '',
			country: '',
			temperature: undefined,
			humidity: undefined,
			description: undefined,
			error: undefined,
			icon: undefined,
		}
	}

	handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
	};

	getWeather = async (event) => {
		event.preventDefault();
		const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${this.state.city},${this.state.country}&units=metric&appid=9b6146ed56aeecdad2247aa0eea8aded`);
		const response = await api_call.json();
		console.log(response);
		if(this.state.city && this.state.country) {
			this.setState({
				city: response.name,
				country: response.sys.country,
				temperature: response.main.temp,
				humidity: response.main.humidity,
				description: response.weather[0].description,
				icon: response.weather[0].icon,
				error: '',
			});
		} else {
			this.setState({
				error: 'Please enter a city and country...'
			})
		}
	}

	render() {
		return(
			<div>
				<h3>WeatherApp</h3>
				<Form 
					loadWeather={this.getWeather}
					city={this.state.city}
					country={this.state.country}
					handleChange={this.handleChange}/>
				<WeatherConditions
					city={this.state.city}
					country={this.state.country}
					temperature={this.state.temperature}
					humidity={this.state.humidity}
					description={this.state.description}
					error={this.state.error}
					/>
			</div>
		);
	}
}

export default WeatherApp;
